mapper.select:
select
,
from tp_user where USER_NO = #{userNo,jdbcType=INTEGER}

select
, o.ORG_ID as orgId,a.AREA_CODE as areaCode,o.org_name as userOrg,a.area_name as areaName,ap.area_code as province_code from tp_user left join tp_org o on USER_ORGID = o.ORG_ID left join tp_area a on USER_AREA_CODE = a.AREA_CODE left join tp_area ap on a.area_parent = ap.area_code
where USER_NAME like #{userName}

select *
FROM tp_user
WHERE user_No = #{userNo}
and user_PWD = #{old_pwd}

mapper.update:
update tp_user
set user_PWD = #{new_pwd,jdbcType=VARCHAR}
WHERE user_No = #{userNo}
